//step1: 设置Diameter链路拥塞控制的告警开关
SET LINKCONGCTRL: ALMSWITCH=ENABLE;
//step2: 配置链路拥塞触发告警的链路拥塞率
SET LINKCONGCTRL: ALMCONGRATE=20;
//step3: 设置用于上报告警的链路拥塞状态检测周期
SET LINKCONGCTRL: ALMRPTDTCTPRD=12;
//step4: 设置用于恢复告警的链路拥塞状态检测周期
SET LINKCONGCTRL: ALMCLRDTCTPRD=12;
//step5: 设置链路重建开关
SET LINKCONGCTRL: REESTABLISHSW=DISABLE;
//step6: 配置触发链路重建的链路拥塞率
SET LINKCONGCTRL: REESTCONGRATE=80;
//step7: 设置用于主动拆链并重建链路的链路拥塞状态检测周期
SET LINKCONGCTRL: REESTABLISHPRD=255;

