ADD URRGROUP:URRGROUPNAME="1234567890_df_ch_p",UPURRNAME1="1234567890_df_offline",DOWNURRNAME1="1234567890_df_offline",UPURRNAME2="1234567890_df_online",DOWNURRNAME2="1234567890_df_online";
ADD PCCPOLICYGRP:PCCPOLICYGRPNM="1234567890_df_pcc_p_g_disable",PCCACTPROPNAME="default_discard",URRGROUPNAME="1234567890_df_ch_p";
ADD PCCPOLICYGRP:PCCPOLICYGRPNM="1234567890_df_pcc_p_g_enable",URRGROUPNAME="1234567890_df_ch_p";
ADD RULE:RULENAME="1234567890disable",POLICYTYPE=PCC,FILTERINGMODE=FLOWFILTER,FLOWFILTERNAME="f_f_df",PRIORITY=65000,POLICYNAME="1234567890_df_pcc_p_g_disable";
ADD RULE:RULENAME="1234567890enable",POLICYTYPE=PCC,FILTERINGMODE=FLOWFILTER,FLOWFILTERNAME="f_f_df",PRIORITY=65001,POLICYNAME="1234567890_df_pcc_p_g_enable";
ADD RULEBINDING:USERPROFILENAME="u_p_5Gto4G.NR",RULENAME="1234567890enable";
ADD SPECURRGRPLIST:URRGROUPNAME="1234567890_df_ch_p";
END
